import "../App.css";
function joinBoard(setPlayer) {
  const textBox = document.getElementById("boardId");
  let boardCode;

  if (textBox) {
    boardCode = textBox.value;
    textBox.value = "";
  } else {
    alert("invalid text box id used in function joinBoard in HomePage.tsx");
  }

  if (!boardCode) {
    alert("please enter a valid name.");
    return;
  }
}

