  return (
    <div id="homePage">
      <h1 id="greetingPlayer">Hi {player.name}!</h1>
      <input type="text" id="boardId" placeholder="Enter Board Code"></input>
      <div id="buttonContainer">
        <button id="joinBoard" onClick={() => joinBoard(setPlayer)}>Join Board</button>
